import COLLEGE_DATA from "./assets/college_with_cluster.json";
import CLUSTER_DATA from "./assets/cluster_with_colleges.json";
import './App.css';
import React, { useState } from 'react'
import DataTable from 'react-data-table-component'
import { COLUMNS } from './components/columns.js';

function App() {

  const [search, setSearch] = useState('');
  const [colleges, setColleges] = useState([]);
  const cluster = Object.values(COLLEGE_DATA).find(e => e.name === search);

  const weights = {
    "A": 10000,
		"B": 1000,
		"C": 100,
		"D": 10,
		"+": 1,
		"-": -1
  }

  const rating_values = CLUSTER_DATA.colleges.HappinessGrade.map( rating => ({
    rating, 
    "value": rating.split('').map(char => weights[char]).reduce((a,b) => a+b)
  }));

  const click = () => {
    const collegeData = Object.values(CLUSTER_DATA).find(e => e.cluster === cluster.cluster)
    setColleges(collegeData)
  }
  const onSearchChange = event => {
    setSearch(event.target.value)
  }

  console.log('colleges')
  console.log(colleges);
  console.log(COLUMNS);
  console.log(rating_values)

  return (
    <div className="App">
      <div className="searchBar">
        <input onChange={onSearchChange} value={search}/>
        <button onClick={click}>Search</button>
      </div>

      {/* <BasicTable colleges={colleges}/> */}

      <div className="container mt-5">
        <DataTable
          columns={COLUMNS}
          data={colleges.colleges}
          fixedHeader
          pagination
        ></DataTable>
      </div>

    </div>
  );
}

export default App;
