import COLLEGE_DATA from "/Users/thinsusan/Desktop/react-app/src/assets/college_with_cluster.json";
import CLUSTER_DATA from "/Users/thinsusan/Desktop/react-app/src/assets/cluster_with_colleges.json";
import './App.css';
import React, { useMemo, useState } from 'react'
import { useTable } from "react-table"; 
// import BasicTable from "./components/BasicTable";
import { COLUMNS } from '/Users/thinsusan/Desktop/react-app/src/components/columns.js';

function App() {

  const [search, setSearch] = useState('');
  const [colleges, setColleges] = useState([]);
  const cluster = Object.values(COLLEGE_DATA).find(e => e.name === search);
  // const colleges = Object.values(CLUSTER_DATA).find(e => e.cluster === cluster.cluster);

  const click = () => {
    alert(search)
    const collegeData = Object.values(CLUSTER_DATA).find(e => e.cluster === cluster.cluster)
    setColleges(collegeData)
    // setColleges(collegeData.colleges)
  }
  const onSearchChange = event => {
    setSearch(event.target.value)
  }

  console.log('colleges')
  console.log(colleges);




  const columns = useMemo(() => COLUMNS, [])
  const data = useMemo(() => colleges, [])

  console.log(data);
  // Brown University

  const tableInstance = useTable({
      columns: columns,
      data: data
  })

  const { 
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
  } = tableInstance





  return (
    <div className="App">
      <input onChange={onSearchChange} value={search}/>
      <button onClick={click}>Search</button>
      {/* <BasicTable colleges={colleges}/> */}


      <table {...getTableProps()}>
            <thead>
                {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                        <th></th>
                    </tr>
                    ))}
                <tr>
                    <th></th>
                </tr>
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map(row => {
                    prepareRow(row)
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map((cell) => {
                                return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                            })
                            }
                            <td></td>
                        </tr>
                    )
                })}
                <tr>
                    <td></td>
                </tr>
            </tbody>
        </table>
    </div>
  );
}

export default App;
