import COLLEGE_DATA from "/Users/thinsusan/Desktop/react-app/src/assets/college_with_cluster.json";
import CLUSTER_DATA from "/Users/thinsusan/Desktop/react-app/src/assets/cluster_with_colleges.json";
import './App.css';
import React, { useMemo, useState } from 'react'
import { useTable } from "react-table"; 
import DataTable from 'react-data-table-component'
// import BasicTable from "./components/BasicTable";
// import { COLUMNS } from '/Users/thinsusan/Desktop/react-app/src/components/columns.js';

function App() {

  const COLUMNS = [
    {
        Header: 'University',
        selector: row => row.name
    },
    {
        Header: 'GPA',
        selector: row => row.GPA
    },
    {
        Header: 'Happiness Grade',
        selector: row => row.HappinessGrade
    },
    {
        Header: 'Happiness Rank',
        selector: row => row.HappinessRank
    }
  ];

  const [search, setSearch] = useState('');
  const [colleges, setColleges] = useState([]);
  const cluster = Object.values(COLLEGE_DATA).find(e => e.name === search);
  // const colleges = Object.values(CLUSTER_DATA).find(e => e.cluster === cluster.cluster);

  const click = () => {
    alert(search)
    const collegeData = Object.values(CLUSTER_DATA).find(e => e.cluster === cluster.cluster)
    setColleges(collegeData)
    // setColleges(collegeData.colleges)
  }
  const onSearchChange = event => {
    setSearch(event.target.value)
  }

  console.log('colleges')
  console.log(colleges);

  return (
    <div className="App">
      <input onChange={onSearchChange} value={search}/>
      <button onClick={click}>Search</button>
      {/* <BasicTable colleges={colleges}/> */}

      <div className="container mt-5">
        <DataTable
          columns={COLUMNS}
          data={colleges.colleges}
        ></DataTable>
      </div>

    </div>
  );
}

export default App;
